	&lt;div class=&quot;col-md-6&quot; data-pg-collapsed=&quot;&quot;&gt;
		&lt;p&gt;&lt;i class=&quot;fa fa-institution&quot;&gt;&lt;/i&gt;&lt;/p&gt;

		&lt;p class=&quot;couleur_A&quot; data-pg-collapsed=&quot;&quot; data-pg-name=&quot;TITRE&quot;&gt;&lt;b&gt;STUDIOS, PARTENAIRES &amp;amp; ENTREPRISES&lt;/b&gt;&lt;br /&gt;
			&amp;nbsp;&lt;/p&gt;

		&lt;p class=&quot;p&quot;&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/p&gt;

		&lt;p data-pg-collapsed=&quot;&quot;&gt;Au sein de mon engagement, je développe des &lt;b&gt;partenariats&lt;/b&gt; avec des entreprises du secteur&lt;b&gt; technologique&lt;/b&gt;,&lt;b&gt; digital&lt;/b&gt; et &lt;b&gt;social &lt;/b&gt;désireuses de s’engager sérieusement dans des&lt;b&gt; moyens alternatifs &lt;/b&gt;de &lt;b&gt;production et de transmission.&amp;nbsp;&lt;/b&gt;&lt;/p&gt;

		&lt;p data-pg-collapsed=&quot;&quot;&gt;&amp;nbsp;&lt;/p&gt;

		&lt;p data-pg-collapsed=&quot;&quot;&gt;&amp;nbsp;&lt;/p&gt;

		&lt;hr class=&quot;hr_footer&quot; data-pg-name=&quot;////////////&quot; /&gt;&lt;/div&gt;

